import {makeAutoObservable} from 'mobx'

class Toolbar {
    toolbar = null;
    canvas = null;
    context = null;
    toggle = false;
    ws = null;
    //wsId = null;
    constructor() {
        makeAutoObservable(this);
    }

    setToolbar(val) {
        this.toolbar = val;
    }

    setWs(ws) {
        this.ws = ws;
    }

    setCanvas(val) {
        this.canvas = val;
        this.context = this.canvas.getContext('2d');
    }

    down = (e) => {
        this.toggle = true;
        this.context.beginPath();
        this.context.moveTo(e.pageX - e.target.offsetLeft, e.pageY - e.target.offsetTop);

    }

    up = (e) => {
        this.toggle = false;
    }

    move = (e) => {
        if (this.toggle) {
            this.context.lineTo(e.pageX - e.target.offsetLeft, e.pageY - e.target.offsetTop);
            this.context.stroke();
            //toolbar.context.fillRect(e.pageX - e.target.offsetLeft, e.pageY - e.target.offsetTop, 2, 2);
        }
    }

    draw(x,y) {
        this.context.beginPath();
        this.context.moveTo(x, y);
        this.context.lineTo(x, y);
        this.context.stroke();
    }
}

export default new Toolbar();
