import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import { Button, ToggleButton, Navbar, Container, NavDropdown, Offcanvas} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useHistory } from 'react-router-dom';
import { BrowserRouter } from 'react-router-dom';
import { routes } from './router/routes';
import { Route } from 'react-router-dom';
import { Redirect, Switch } from 'react-router-dom/cjs/react-router-dom.min';
import { Left } from './components/ui-components/Left';
import { History } from './components/History';
import { Menu } from './components/Menu';

import { Footer } from './components/Footer';
import cl from './styles/App.module.css';
function App() {

  let [state, setState] = useState(1);
  let [show, setShow] = useState(false);
  const handleShow = () => {
    setShow(prev => !prev);
  }


  return (
    <BrowserRouter>
        <div className={cl.App}>
          <div>
            <Navbar bg="light">
            <Container>
            <Button onClick={handleShow} variant="warning">Left menu</Button>
            </Container>
            <Menu/>

            </Navbar>
            <Left show={show} handleShow={handleShow}/>
            <Switch>
            {
            routes.map(route =>
                <Route
                  path={route.path}
                  exact={route.exact}
                  component={route.component}
                  key={route.path}
                />
            )
            }
            <Redirect to='/base'/>
            </Switch>
          </div>
          <Footer/>
        </div>
    </BrowserRouter>
  )
}

export default App;
