import { useContext, useEffect, useRef, useState } from "react";
import cl from '../styles/Canvas.module.css';
import toolbar from "../store/toolbar";
import { observer } from "mobx-react-lite";
const Test = observer(() => {
    const canvas = useRef();
    //const ctx =  canvas.getContext('2d');
    const [top, setTop] = useState(0);
    const [left, setLeft] = useState(0);
    useEffect(() => {
        toolbar.setCanvas(canvas.current);
        let ws = new WebSocket('ws://localhost:5000/');
        toolbar.setWs(ws);

        toolbar.ws.onopen = () => {
            console.log('opened');
            const data = {
                id: (Math.random() * 10).toFixed(0),
                method: 'connection'
            }
            toolbar.ws.send(JSON.stringify(data));
        }

        toolbar.ws.onmessage = (data) => {
            console.log('mes', data);
            data = JSON.parse(data.data);
            switch (data.method) {
                case 'paint':
                    toolbar.draw(data.x, data.y, data.action);
                    setTop(data.y);
                    setLeft(data.x);
            }
        }
        //console.log('canvas', toolbar.canvas);
    }, []);
    const [toggle, setToggle] = useState(false);



    return (
        <div style={{display: 'flex', justifyContent: 'center', alignItems: 'center'}}>
            <canvas 
                width={window.innerWidth - 50}
                height={window.innerHeight - 200}
                className={cl.Canvas}
                ref={canvas}
                onMouseDown={toolbar.down}
                onMouseUp={toolbar.up}
                onMouseMove={toolbar.move}
                >
            </canvas>
            <div className={cl.Name} style={{display: 'none', top, left}}>{'ffsf'}</div>
        </div>

    )
});

export default Test
