import {makeAutoObservable} from 'mobx'

class Toolbar {
    toolbar = null;
    canvas = null;
    context = null;
    toggle = false;
    constructor() {
        makeAutoObservable(this);
    }

    setToolbar(val) {
        this.toolbar = val;
    }

    setCanvas(val) {
        this.canvas = val;
        this.context = this.canvas.getContext('2d');
    }

    down = (e, toggle) => {
        setToggle(true);
        toolbar.context.beginPath();
        toolbar.context.moveTo(e.pageX - e.target.offsetLeft, e.pageY - e.target.offsetTop)
    }

    up = (e) => {
        setToggle(false);
    }

    move = (e) => {
        if (toggle) {
            toolbar.context.lineTo(e.pageX - e.target.offsetLeft, e.pageY - e.target.offsetTop);
            toolbar.context.stroke();
            //toolbar.context.fillRect(e.pageX - e.target.offsetLeft, e.pageY - e.target.offsetTop, 2, 2);
        }
    }

