import {makeAutoObservable} from 'mobx'

class Toolbar {
    toolbar = null;
    canvas = null;
    constructor() {
        makeAutoObservable(this);
    }

    setToolbar(val) {
        this.toolbar = val;
    }

    setCanvas(val) {
        this.canvas = val;
    }

    setContext(val) {
        this.canvas = val.getContext('2d');
    }
}

export default new Toolbar();
