import Name from "./Name";
const Test = observer(({children}) => {
    const canvas = useRef();
    //const ctx =  canvas.getContext('2d');
    const [top, setTop] = useState(0);
    const [left, setLeft] = useState(0);
    const [display, setDisplay] = useState('none');
    useEffect(() => {
        toolbar.setCanvas(canvas.current);
        let ws = new WebSocket('ws://localhost:5000/');
        toolbar.setWs(ws);

        toolbar.ws.onopen = () => {
            console.log('opened');
            let id = (Math.random() * 100).toFixed(0);
            const data = {
                id: id,
                method: 'connection',
                name: 'client' + id
            }
            toolbar.ws.send(JSON.stringify(data));
        }

        toolbar.ws.onmessage = (data) => {
            console.log('mes', data);
            data = JSON.parse(data.data);
            switch (data.method) {
                case 'paint':
                    toolbar.draw(data.x, data.y, data.action);
                    //toolbar.changePositionByName(data.name, data.x, data.u + 100);

                    // setTop(data.y + 100);
