import { useSelector } from 'react-redux';
import {Header} from '../ui-components/Header'
// import cl from '../../../styles/Info.module.css';
import { countries } from '../dataBase/countries';
import { useState } from 'react/cjs/react.development';
// import Image from '../../ui-components/Image';
import { DropdownButton, Image } from 'react-bootstrap';
import fs from 'fs';
import { useEffect } from 'react';
import axios from 'axios';
import { TableComp } from '../ui-components/TableComp';
import { TableChars } from '../ui-components/TableChars';
import { Modal, Button } from 'react-bootstrap';
//const fetch = require('node-fetch');
import cl from '../../styles/Countries.module.css'
import { AddModal } from '../modals/AddModal';
export const Countries = (props) => {

    //let arr = countries;
    // let jsx = '';
    // for (let el of arr)
    // {
    //     let fields = el.to;
    //     jsx += <p>{el.name}</p>
    //     for (let fi of fields)
    //         jsx += <li>{fi}</li>
    // }

    let init = []
    const [body, setBody] = useState(init);
    const [str, setStr] = useState({ country: 'Россия', name: '' });
    const [isload, setLoad] = useState(false);
    const [countNames, setCountNames] = useState([]);
    const [arr, setArr] = useState([]);
    const [countries, setCountries] = useState({});
    const [urlName, setUrlName] = useState('');
    const [name, setName] = useState('');
    const [fname, setFname] = useState('');
    const [capture, setCaptureFile] = useState({});
    const [visible, setVisible] = useState(false);
    const [showModal, setShowModal] = useState(false);
    const [showAddModal, setShowAddModal] = useState(false);
    const [country, setCountry] = useState('Россия')
    const [chars, setChars] = useState({});
    const bodyStatic = ['Зига', "Твой", "Дом"];

    const [sort, setSort] = useState(['По имени', "По дате"])
    console.log('country', country);
    useEffect(async () => {
        setLoad(false);
        let fetchCounts;
        fetchCounts = await axios.get('http://localhost:5000/countries');
        fetchCounts = fetchCounts.data.countries;
        setCountries(fetchCounts);

        setArr(fetchCounts.map(el => {
            return (<div>
                <p>{el.name}</p>
                {
                    el.to.map(name => <li onMouseOver={() => showPic(true, name)} onMouseOut={() => showPic(false, name)}>{name}</li>)
                }
            </div>);
        }));
        setCountNames(fetchCounts.map(el => el.name));
        setLoad(true);
        console.log('countries', countries);
    }, [countries.length]);

    //console.log(jsx)                 {
    //                 countNames.map(el => <option>{el}</option>)
    //                }
    const showPic = (flag, name) => {
        console.log('name', name);
        setUrlName(`http://localhost:5000/image?name=${name}`);
        setVisible(flag);

    }

    // const add = async () => {
    //     setBody(prev => [...prev, { country: str.country, name: str.name }]);
    //     let fetchCounts = await axios.get(`http://localhost:5000/add?country=${str.country}&name=${str.name}`);
    //     fetchCounts = fetchCounts.data;
    //     setCountries(fetchCounts);
    //     setArr(fetchCounts.map(el => {
    //         return (<div>
    //             <p>{el.name}</p>
    //             {
    //                 el.to.map(name => <li onMouseOver={() => showPic(true, name)} onMouseOut={() => showPic(false, name)}>{name}</li>)
    //             }
    //         </div>);
    //     }));
    // }

    const deleteById = async (name) => {
        setBody(prev => prev.filter(el => el.name != name));

        let fetchCounts = await axios.get(`http://localhost:5000/delete?country=${str.country}&name=${str.name}`);
        fetchCounts = fetchCounts.data;
        setCountries(fetchCounts);
        setArr(fetchCounts.map(el => {
            return (<div>
                <p>{el.name}</p>
                {
                    el.to.map(name => <li onMouseOver={() => showPic(true, name)} onMouseOut={() => showPic(false, name)}>{name}</li>)
                }
            </div>);
        }));
        // setCountries(prev => {
        // console.log('delete name', name);
        // setBody(prev => prev.filter(el => {
        //     console.log('el', el);
        //     return !String(el).includes(name);
        // }
        //     ));
    }

    const setNameFunc = (e) => {
        setStr(prev => {
            return { ...prev, name: e.target.value };
        })
    }

    const getImage = async (name) => {
        console.log('infunc');
         let img = (await axios.get(`http://localhost:5000/image?name=${name}`)).data;
         console.log('img', img);
         return img;
    }

    const openModal = async (name) => {
        console.log('aame', name);
        setShowModal(true);
        setName(name);
        let chars = (await axios.get(`http://localhost:5000/info?name=${name}`)).data;
        //console.log('chars', chars);
        setChars(chars);
    }

    const addNote = () => {
        setShowAddModal(true);
    }

    const add = () => {
        console.log('capt', capture);
        const data = new FormData();
        data.append('file', capture);
        //capture.map(dat => URL.createObjectURL(dat));
        let url = URL.createObjectURL(capture);
        console.log('url', url);
        axios.post('http://localhost:5000/upload', data, {
            headers: {
                "Content-type": "form/multipart"
            }
        });

    }
    return (
        isload ?
            <div className={cl.Countries}>
                <Header name="Страны лидеры по производству БПЛА" />
                <div>
                    <div style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-between'}}>
                        {
                        countries.length ?
                        (
                        countries.map(count => {
                            return (
                                <TableComp
                                    style={{width: '30px'}}
                                    header={count.name}
                                    body={count.to}
                                    onOver={(flag, name) => showPic(flag, name)}
                                    onOut={(flag, name) => showPic(flag, name)}
                                    onClick={(name) => openModal(name)}
                                    propFlag={true}
                                />
                            )

                        })
                        )
                        :
                        <p>Ждите</p>
                        }
                    </div>
                    <Button style={{margin: '10px'}} onClick={addNote}>+</Button>
                    <DropdownButton
                        className="mb-3"
                        title={sort}
                        onSelect={e => setSort(e)}
                    >
                    </DropdownButton>
                </div>
                    <div style={{display: visible ? 'flex' : 'none'}}>
                        <Image src={urlName} style={{width: '320px', height: '240px'}} />
                    </div>
                    <Modal
                        show ={ showModal }
                        onHide={() => setShowModal(false)}
                        backdrop =" static "
                        keyboard ={ false }
                    >
                        < Modal.Header closeButton >
                        < Modal.Title > {name}</ Modal.Title >
                        </ Modal.Header >
                        < Modal.Body >
                            <TableChars
                                header={name}
                                body={chars}
                            />
                            <Image src={urlName} style={{width: '320px', height: '240px', margin: '10px'}} />
                        </Modal.Body >
                    </ Modal >

                    <AddModal
                        show={showAddModal}
                        setShow={setShowAddModal}
                        setItem={setCountry}
                        item={country}
                        title="Добавить"
                        countries={countNames}
                        name={name}
                        setName={setName}
                        fname={fname}
                        setFname={setFname}
                        setCaptureFile={setCaptureFile}
                        capture={capture}
                        add={add}
                    />
            </div>
            :
            <div>
                Падажжи
            </div>

    );
}
